  overflow: hidden;
  color: #303133;
  padding-left: 4px;
  box-sizing: unset;
}
.gg-form-item-right {
  /* width: calc(100% - 60px - 4px - 4px - 20px); */
  width: 100%;
  padding-left: 5px;
