  transform: rotate(-180deg);
}
.gg-form-item-right-item-icon-item {
  width: 0;
  height: 0;
  border-left: 7.5px solid transparent;
  border-right: 7.5px solid transparent;
  border-top: 11px solid #c0c4cc;
}
