  font-size: 12px;
  color: #fa3534;
  margin-top: 6px;
  line-height: 12px;
}
.gg-form-item-right-col {
  /* padding-left: 0; */
  width: auto;
}
.gg-form-item-right-tip {
  font-size: 12px;
  color: rgba(0, 0, 0, 0.4);
  /* margin-top: 5px; */
  line-height: 1.5em;
}
.gg-form-item-lebel-title-must {
  color: #fa3534;
  /* margin-right: 5px; */
  position: absolute;
  top: 15px;
  left: 8px;
}
.gg-form-item-lebel-tip {
  color: rgba(0, 0, 0, 0.4);
}
.gg-form-item-right-item-right {
